mapper.select:
        SELECT


--         级联表的字段
        ,xiangmu.jiaoshi_id as xiangmuJiaoshiId
        ,xiangmu.xiangmu_name as xiangmuName
        ,xiangmu.xiangmu_types as xiangmuTypes
        ,xiangmu.xiangmu_file as xiangmuFile
        ,xiangmu.xiangmu_content as xiangmuContent
        ,xiangmu.xiangmu_delete as xiangmuDelete
        ,xiangmu.xiangmu_yesno_types as xiangmuYesnoTypes
        ,xiangmu.xiangmu_yesno_text as xiangmuYesnoText


        ,dictionary.beizhu as xiangmuTypesBeizhu

        FROM gongzuoliang  a
        left JOIN xiangmu xiangmu ON a.xiangmu_id = xiangmu.id
        left JOIN dictionary dictionary ON xiangmu.xiangmu_types = dictionary.code_index



                and a.id in

                #{item}



                and (
                    a.xiangmu_id = #{params.xiangmuId}
                )


                and a.gongzuoliang_content like CONCAT('%',#{params.gongzuoliangContent},'%')


                  and a.tijiaozongshu >= #{params.tijiaozongshuStart}   


                  and a.tijiaozongshu <= #{params.tijiaozongshuEnd}   


                and a.tijiaozongshu = #{params.tijiaozongshu}


                  and a.zonggongzuoliang >= #{params.zonggongzuoliangStart}   


                  and a.zonggongzuoliang <= #{params.zonggongzuoliangEnd}   


                and a.zonggongzuoliang = #{params.zonggongzuoliang}


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




                and a.xiangmu_id IS NOT NULL


                and xiangmu.jiaoshi_id = #{params.jiaoshiId}


                and xiangmu.xiangmu_name like CONCAT('%',#{params.xiangmuName},'%')


                and xiangmu.xiangmu_types = #{params.xiangmuTypes}



                and xiangmu.xiangmu_content like CONCAT('%',#{params.xiangmuContent},'%')


                  and xiangmu.xiangmu_delete >= #{params.xiangmuDeleteStart}   


                  and xiangmu.xiangmu_delete <= #{params.xiangmuDeleteEnd}   


                and xiangmu.xiangmu_delete = #{params.xiangmuDelete}


                  and UNIX_TIMESTAMP(xiangmu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(xiangmu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


                and xiangmu.xiangmu_yesno_types = #{params.xiangmuYesnoTypes}



                and xiangmu.xiangmu_yesno_text like CONCAT('%',#{params.xiangmuYesnoText},'%')


                  and UNIX_TIMESTAMP(xiangmu.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


                  and UNIX_TIMESTAMP(xiangmu.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 



                and dictionary.dic_code = "xiangmu_types"



        order by a.${params.orderBy} desc
